from __future__ import absolute_import

from geogrid.geom import LATITUDE_MAX
from geogrid.geom import LATITUDE_MIN
from geogrid.geom import LONGITUDE_MAX
from geogrid.geom import LONGITUDE_MIN

try:
    import geogrid.cgeogrid_wrapper as implementation
    implementation  # pyflakes
except ImportError:
    # May happen on e.g. PyPy (where a C module just for math isn't very useful
    # anyway)
    import geogrid.geogrid_reference as implementation

GeoGrid = implementation.GeoGrid
deinterleave32 = implementation.deinterleave32
geogrid_for_size = implementation.geogrid_for_size
interleave32 = implementation.interleave32


__version__ = '1.0.8'

_pyflakes_ignore = [GeoGrid, geogrid_for_size, deinterleave32, interleave32, LATITUDE_MAX, LATITUDE_MIN, LONGITUDE_MAX, LONGITUDE_MIN]
