# -*- coding: utf-8 -*-
import pyramid
import uwsgi_metrics


def includeme(config):
    uwsgi_metrics.initialize()
    config.add_tween("pyramid_uwsgi_metrics.tweens.metrics_tween_factory",
                     under=pyramid.tweens.INGRESS)
    config.add_route('status.metrics', '/status/metrics')
    config.scan('pyramid_uwsgi_metrics.views')
